$("#customer_tag_update_enter").on("click",function(){window.confirm("荷主タグを更新しますか？")==!0&&$("#customer_tag_update_form").submit()});$(".tag_delete").on("click",function(){$(this).remove()});$("#tag_add").on("click",function(){const t=$("#select_tag").val(),e=$("#select_tag option:selected").html();try{if($("#setting_tag_div").find(`[id=${t}]`).length!==0)throw new Error("既に存在するタグです。");$("#setting_tag_div").append(`<input type='text' id=${t} name='tag_id[${t}]' class='tag_delete text-xs bg-theme-main text-white border-none col-span-4 text-center rounded-md cursor-pointer' data-tag_id=${t} value=${e} readonly >`)}catch(n){alert(n.message)}});
